import {createWrapper} from 'next-redux-wrapper';
import {applyMiddleware, compose, createStore} from 'redux';
import { composeWithDevTools} from 'redux-devtool-extension';
import reducer from '../reducers/index'

const configureStore = () => {

  //ê°œë°œìš© ë°°í¬ìš© ë¯¸ë“¤ì›¨ì–´ê°€ ë‹¤ë¥´ë‹¤. npm i redux-devtools-extension
  // ë°ì´í„°ê°€ ë‹¤ë³´ì—¬ì„œ ë³´ì•ˆì— ì·¨ì•½í• ìˆ˜ê°€ ìˆê¸° ë•Œë¬¸ì— ê°œë°œìš©ì¼ë•ŒëŠ” ê¸°ë¡ë³´ì´ê²Œ, ë°°í¬ìš©ì¼ë•ŒëŠ” ì•ˆë³´ì´ê²Œ í•œë‹¤. 
  const middlewares = [];//saga || thunk ë„£ëŠ”ê³³.
  const enhancer = process.env.NODE_ENV === 'production'
  ? compose(applyMiddleware(...middlewares))
  : composeWithDevTools(applyMiddleware(...middlewares))

  const store = createStore(reducer,enhancer);
  store.dispatch({
    type : 'CHANGE_NICKNAME',
    data: '2sook0'
  })
  return store;
}

const wrapper = createWrapper(configureStore,{
  debug : process.env.NODE_ENV === 'development',
});
export default wrapper;
