const initialState = {
  user : {
    isLoggedIn : false,
    user : null,
    signupData : {},
    loginData : {},
  },
  post : {
    mainPosts : []
  }
}
const login = (data) => {
  return {
    type : 'LOG_IN',
    data : data
  }
}
/*const changeNickname = {
  type : 'CHANGE_NICKNAME',
  data : '2sook0' -> 요부분이 바뀔때마다 하나씩 액션생성? 너무 비효율적.
}
//데이터가 바뀔때마다 액션을 만들어줘야하는데 ,비슷한객체가 반복되면 액션을 함수화 시키는것이 좋다.
//action creator
