  return {
    type : 'LOG_IN',
    data : data
  }
}
changeNickname('2sook0')
store.dispatch(changeNickname('hello'))
*/
//(이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
  switch(action.type){
    case 'LOG_IN' :
      return {
        ...state,
        user : {
          ...state.user, //안바꾸고싶은것은 참조관계
          isLoggedIn : true, // 바꾸고싶은것만 명확하게
        }
      }
  }
}

export default rootReducer;
