import React, { useState } from 'react'
import PropTypes from 'prop-types';
import Slick from 'react-slick'
import {Overlay, Header, CloseBtn, SlickWrapper, ImgWrapper,Indicator,Global} from './style'

const ImagesZoom = ({images, onClose}) => {
  const [currentSlide , setCurrentSlide] = useState(0)
  return (
  <Overlay>
    <Global/>
    <Header>
      <h1>ÏÉÅÏÑ∏Ïù¥ÎØ∏ÏßÄ</h1>
      <CloseBtn onClick = {onClose}/> 
    </Header>
    <SlickWrapper>
      <div>
        <Slick
          initialSlide = {0} //Ïù∏Îç±Ïä§ 0Î≤àÏß∏Î∂ÄÌÑ∞ ÏãúÏûë
          afterChange = {(slide)=>setCurrentSlide(slide)} //pageÎ•º ÎÑòÍ∏∞Î©¥ Ïä¨ÎùºÏù¥ÎìúÏóê Î≤àÌò∏Î•º Ï§ÄÎã§.Í∑∏Í±∏ Ïä§ÌÖåÏù¥Ìä∏Î°ú Ï†ÄÏû•.
          infinite 
          arrows = {false}
          slidesToShow = {1} //ÌïúÎ≤àÏóê ÌïòÎÇòÏî©Îßå ÎÑòÍ∏∏ÏàòÏûàÍ≤å
          slidesToScroll = {1}
        >
          {images.map((el)=>{
            return <ImgWrapper key = {el.src}>
              <img src = {el.src} alt = {el.src} />
            </ImgWrapper>
          })}
        </Slick>
        <Indicator>
          <div>
            {currentSlide + 1}
            {` `}
            /
            {images.length}
          </div>
        </Indicator>
      </div>
    </SlickWrapper>
  </Overlay>
  )
}

ImagesZoom.propTypes = {
  images: PropTypes.arrayOf(PropTypes.shape({
    src: PropTypes.string,
  })).isRequired,
  onClose: PropTypes.func.isRequired,
};

export default ImagesZoom;
