import React, { useState, useCallback } from 'react'
import AppLayOut from "../components/AppLayOut"
import {Form,Input,Checkbox,Button} from 'antd'
import Head from 'next/head'
import useInput from '../hooks/useInput'
import styled from 'styled-components'

const ErrMsg = styled.div`
color : red;
`

const Signup = () => {

  const [id, onChangeId] = useInput('');
  const [nickname, onChangeNickname] = useInput('');
  const [pw, onChangePw] = useInput('');
  const [pwchk, setPwchk] = useState('');
  const [pwchkErr, setPwchkErr] = useState(false);
  const [term, setTerm] = useState(''); 
  const [termErr, setTermErr] = useState(false); 


  const onChangePwchk = useCallback((e)=>{
    setPwchk(e.target.value); //Ïó¨Í∏∞ÍπåÏßÑ useInput Í≥º Í∞ôÏßÄÎßå ÌïúÎ≤àÎçîÏò§Î•òÏ≤¥ÌÅ¨
    setPwchkErr(e.target.value !== pw)
  },[pw])
  const onChangeTerm = useCallback((e)=>{
    setTerm(e.target.checked);
    setTermErr(false);
  },[])

  const onSubmit = useCallback(()=>{
    if(pw !== pwchk) return setPwchkErr(true);
    if(!term) return setTermErr(true);
    console.log(id,nickname,pw) //ÏÑúÎ≤ÑÎ°ú Î≥¥ÎÇ¥Îäî Îç∞Ïù¥ÌÑ∞Îì§ Ïûò Ïã§ÌñâÎêòÎäîÏßÄ ÏΩòÏÜîÌôïÏù∏ -> Ïù¥Í±∞Î•º ÏÑúÎ≤ÑÏóê Î≥¥ÎÇ¥Ï£ºÎ©¥ ÎêúÎã§. 
  },[pw,pwchk,term])

  return (
    <AppLayOut>
      <Head>
        <title>ÌöåÏõêÍ∞ÄÏûÖ - NodeBird</title>
      </Head>
      <Form onFinish = {onSubmit}>
        <div>
          <label htmlFor = 'user-id'>ÏïÑÏù¥Îîî</label>
          <br/>
          <Input name = 'user-id' value = {id} required onChange = {onChangeId}/>
        </div>

        <div>
          <label htmlFor = 'user-nickname'>ÎãâÎÑ§ÏûÑ</label>
          <br/>
          <Input name = 'user-nickname' value = {nickname} required onChange = {onChangeNickname}/>
        </div>

        <div>
          <label htmlFor = 'user-pw'>ÎπÑÎ∞ÄÎ≤àÌò∏</label>
          <br/>
          <Input name = 'user-pw' value = {pw} required onChange = {onChangePw}/>
        </div>

        <div>
          <label htmlFor = 'user-pwchk'>ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏</label>
          <br/>
          <Input name = 'user-pwchk' value = {pwchk} required onChange = {onChangePwchk}/>
          {pwchkErr && <ErrMsg>ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.</ErrMsg>}
        </div>
        <div>
          <Checkbox name = 'user-term' checked = {term} onChange = {onChangeTerm}>Î¶¨Ïï°Ìä∏ Í≥µÎ∂ÄÏó¥Ïã¨ÌûàÌï†Í≤ÉÏùÑ ÎèôÏùòÌï©ÎãàÎã§.</Checkbox>
          {termErr && <ErrMsg>ÏïΩÍ¥ÄÏóê ÎèôÏùòÌïòÏÖîÏïº Ìï©ÎãàÎã§.</ErrMsg>}
        </div>    
        <div style = {{marginTop : 10}}>
          <Button type = 'primary' htmlType = 'submit'>Í∞ÄÏûÖÌïòÍ∏∞</Button>
        </div>
      </Form>
    </AppLayOut>)
}

export default Signup;
