import React, { useState } from 'react'
import PropTypes from 'prop-types';
import Slick from 'react-slick'
import { CloseOutlined } from '@ant-design/icons';
import styled, { createGlobalStyle } from 'styled-components'

const Overlay = styled.div`
position:fixed;
z-index : 5000;
top : 0;
left : 0;
right : 0;
bottom : 0;
`
const Header = styled.header`
height : 44px;
background : white;
position : relative;
padding : 0;
text-align : center;

&h1{
  margin : 0;
  font-size: 17px;
  color : #333;
  line-height: 44px;
  cursor: pointer;
}
`
const CloseBtn = styled(CloseOutlined)`
  position : absolute;
  right : 0;
  top : 0;
  padding :15px;
  line-height: 14px;
  cursor: pointer;
`

const SlickWrapper = styled.div`
  height : calc(100%-44px);
  background : #090909;
`
const ImgWrapper = styled.div`
  padding : 32px;
  text-align: center;
  &img{
    margin : 0 auto;
    max-height: 550px;
  }
`
const Indicator = styled.div`
  text-align: center;
  &>div{
    width : 75px;
    height : 30px;
    line-height: 30px;
    border-radius: 15px;
    background: #313131;
    display: inline-block;
    text-align: center;
    color : white;
    font-size : 15px;
  }
`
const Global = createGlobalStyle`
  .slick-slide{
    display: inline-block;
  }
  .ant-card-cover{
    transform : none !important;
  }
`

const ImagesZoom = ({images, onClose}) => {
  const [currentSlide , setCurrentSlide] = useState(0)
  return (
  <Overlay>
    <Global/>
    <Header>
      <h1>ÏÉÅÏÑ∏Ïù¥ÎØ∏ÏßÄ</h1>
      <CloseBtn onClick = {onClose}/> 
    </Header>
    <SlickWrapper>
      <div>
        <Slick
          initialSlide = {0} //Ïù∏Îç±Ïä§ 0Î≤àÏß∏Î∂ÄÌÑ∞ ÏãúÏûë
          afterChange = {(slide)=>setCurrentSlide(slide)} //pageÎ•º ÎÑòÍ∏∞Î©¥ Ïä¨ÎùºÏù¥ÎìúÏóê Î≤àÌò∏Î•º Ï§ÄÎã§.Í∑∏Í±∏ Ïä§ÌÖåÏù¥Ìä∏Î°ú Ï†ÄÏû•.
          infinite 
          arrows = {false}
          slidesToShow = {1} //ÌïúÎ≤àÏóê ÌïòÎÇòÏî©Îßå ÎÑòÍ∏∏ÏàòÏûàÍ≤å
          slidesToScroll = {1}
        >
          {images.map((el)=>{
            return <ImgWrapper key = {el.src}>
              <img src = {el.src} alt = {el.src} />
            </ImgWrapper>
          })}
        </Slick>
        <Indicator>
          <div>
            {currentSlide + 1}
            {` `}
            /
            {images.length}
          </div>
        </Indicator>
      </div>
    </SlickWrapper>
  </Overlay>
  )
}

ImagesZoom.propTypes = {
  images: PropTypes.arrayOf(PropTypes.shape({
    src: PropTypes.string,
  })).isRequired,
  onClose: PropTypes.func.isRequired,
};

export default ImagesZoom;
