import { Button ,Card } from 'antd'
import { Content } from 'antd/lib/layout/layout'
import { RetweetOutlined ,HeartOutlined , MessageOutlined , Popover, EllipsisOutlined} from '@ant-design/icons/RetweetOutlined'
import PropTypes from 'prop-types';
import React from 'react'
import ButtonGroup from 'antd/lib/button/button-group'

const PostCard = ({post}) => {
  const {me} = useSelector((state)=>state.user)
  return (
    <div>
      <Card cover = {post.Image[0] 
      && <PostImages images = {post.Images}/>}
      //jsx에서 배열안에는 key를 넣어주어야 한다. ex)map 
      actions = {[
        <RetweetOutlined key = 'retweet'/>,
        <HeartOutlined key = 'heart'/>,
        <MessageOutlined key = 'comment'/>,
        <Popover key = 'more' content = {(
          <ButtonGroup>
            {}
            <Button>수정</Button>
            <Button type = 'danger'>삭제</Button>
            <Button>신고</Button>
          </ButtonGroup>
        )}>
          <EllipsisOutlined />
        </Popover>
      ]}
      >
        <Image />
        <Content/>
        <Buttons></Buttons>
      </Card>
      <CommentForm/>
      <Comments/>
    </div>
  )
}

PostCard.propTypes = {
  //obj 는 shape 로 더 구체적으로 적어줄 수 있다. 
  post : PropTypes.shape({
    id : PropTypes.number,
    User : PropTypes.object,
    content : PropTypes.string,
    createdAt : PropTypes.object,
    Comments : PropTypes.arrayOf(PropTypes),
    images : PropTypes.arrayOf(PropTypes.object),
  }).isRequired
}

export default PostCard;
