import React from 'react'
import PropTypes from 'prop-types'
import Link from 'next/link'
import { Menu,Input,Row,Col } from 'antd';

//앤트디자인은 grid 지원해준다 : Row,Col
//1/가로먼저 자르고 세로를 잘라준다.
//2/반응형 할땐 무조건 모바일->태블릿->PC 순으로 해야한다. 
//xs : mobile / sm : tablet / md : 작은 데스크탑
//gutter : 간격 -> 이거 프론트에서는 엄청많이 쓰인다. 

const AppLayOut = ({children}) => {
  return (
    <div>
      <Menu mode = 'horizontal'>

        <Menu.Item>
        <Link href = '/'><a>노드버드</a></Link>
        </Menu.Item>

        <Menu.Item>
        <Link href = '/profile'><a>프로필</a></Link>
        </Menu.Item>

        <Menu.Item>
          {/*해시태그 설정 : Input.search */}
        <Input.Search enterButton style = {{verticalAlign :'middle'}}/>
        </Menu.Item>

        <Menu.Item>
        <Link href = '/signup'><a>회원가입</a></Link>
        </Menu.Item>        
      </Menu>

      <Row gutter = {8}>
          <Col xs = {24} sm = {6} md = {6}>
            왼쪽
          </Col>
          <Col xs = {24} md = {12}>
            {children}
          </Col>
          <Col xs = {24} md = {6}>
            <a href= 'https://velog.io/@22soook00' target = '_blank' rel = 'noreferrer noopener'>숙영블로그</a>
          </Col>
        </Row>
    </div>
  )
};
AppLayOut.propTypes = {
  children : PropTypes.node.isRequired
}

export default AppLayOut;
