import React, { useState } from 'react'
import Slick from 'react-slick'
import PropTypes from 'prop-types';
import styled, { createGlobalStyle } from 'styled-components'

const Overlay = styled.div`
position:fixed;
z-index : 5000;
top : 0;
left : 0;
right : 0;
bottom : 0;
`

const Header = styled.header`
height : 44px;
background : white;
position : relative;
padding : 0;
text-align : center;

&h1{
  margin : 0;
  font-size: 17px;
  color : #333;
  line-height: 44px;
  cursor: pointer;
}
&button{
  position : absolute;
  right : 0;
  top : 0;
  padding :15px;
  line-height: 14px;
  cursor: pointer;
}
`
const CloseBtn = styled(closeOutlined)`
  position : absolute;
  right : 0;
  top : 0;
  padding :15px;
  line-height: 14px;
  cursor: pointer;
`

const SlickWrapper = styled.div`
  height : calc(100%-44px);
  background : #090909;
`
const ImgWrapper = styled.div`
  padding : 32px;
  text-align: center;
  &img{
    margin : 0 auto;
    max-height: 750px;
  }
`
const Indicator = styled.div`
  text-align: center;
  &>div{
    width : 75px;
    height : 30px;
    line-height: 30px;
    border-radius: 15px;
    background: #313131;
    display: inline-block;
    text-align: center;
    color : white;
    font-size : 15px;
  }
`
const Global = createGlobalStyle`
  .slick-slide{
    display: inline-block;
  }
  .ant-card-cover{
    transform : none !important;
  }
`

const ImagesZoom = ({images, onClose}) => {
  const [currentSlide , setCurrentSlide] = useState(0)
  return (
  <Overlay>
    <Global/>
    <Header>
      <h1>상세이미지</h1>
      <CloseBtn onClick = {onClose}> &:times </CloseBtn>
    </Header>
    <SlickWrapper>
      <div>
        <Slick
          initialSlide = {0} //인덱스 0번째부터 시작
          afterChange = {(slide)=>setCurrentSlide(slide)} //page를 넘기면 슬라이드에 번호를 준다.그걸 스테이트로 저장.
          infinite 
          arrows = {false}
          slidesToShow = {1} //한번에 하나씩만 넘길수있게
          slidesToScroll = {1}
        >
          {images.map((el)=>{
            return <ImgWrapper key = {el.src}>
              <img src = {el.src} alt = {el.src} />
            </ImgWrapper>
          })}
        </Slick>
      </div>
    </SlickWrapper>
  </Overlay>
  )
}

ImagesZoom.propTypes = {
  images : PropTypes.arrayOf(PropTypes.object).isRequired,
  onClose : PropTypes.func.isRequired,
}

export default ImagesZoom;
