        value = {password} 
        type = 'password'
        onChange = {onChangePassword} required></Input>
      </div>

      <BtnWrapper>
        <Button type = 'primary' htmlType = 'submit' loading = {false} >ë¡œê·¸ì¸</Button>
        <Link href = '/signup'><a><Button>íšŒì›ê°€ì…</Button></a></Link>
      </BtnWrapper>


    </FormWrapper>
    </>
}

export default LoginForm;
