import PropTypes from 'prop-types';
import React from 'react'
import { useSelector } from 'react-redux'
import { Button ,Card, Avatar, Popover } from 'antd'
import { RetweetOutlined ,HeartOutlined , MessageOutlined , EllipsisOutlined} from '@ant-design/icons/RetweetOutlined'

import ButtonGroup from 'antd/lib/button/button-group'

const PostCard = ({post}) => {
  //optional 체이닝 : ~~?.~~ 아이디가 있으면 me 가들어가고 없으면 언디파인이 들어간다.
  const id = useSelector((state)=>state.user.me ?.id);
  return (
    <div>
      <Card cover = {post.Image[0] 
      && <PostImages images = {post.Images}/>}
      //jsx에서 배열안에는 key를 넣어주어야 한다. ex)map 
      actions = {[
        <RetweetOutlined key = 'retweet'/>,
        <HeartOutlined key = 'heart'/>,
        <MessageOutlined key = 'comment'/>,
        <Popover key = 'more' content = {(
          <Button.Group>
            {/*로그인했고, 내 아이디가 아이디와 같으면 수정삭제가능, 아니면 신고가능 */}
            {id && post.User.id === id ? (
              <>
              <Button>수정</Button>
              <Button type = 'danger'>삭제</Button>
              </>
            ) : <Button>신고</Button>}
          </Button.Group>
        )}>
          <EllipsisOutlined />
        </Popover>
      ]}
      >
        <Card.Meta
        avatar = {<Avatar>{post.User.nickname[0]}</Avatar>}
        title = {post.User.nickname}
        description = {post.content}
        />
      </Card>
      {/*<CommentForm/>
      <Comments/>*/}
    </div>
  )
}

PostCard.propTypes = {
  //obj 는 shape 로 더 구체적으로 적어줄 수 있다. 
  post : PropTypes.shape({
    id : PropTypes.number,
    User : PropTypes.object,
    content : PropTypes.string,
    createdAt : PropTypes.object,
    Comments : PropTypes.arrayOf(PropTypes),
    images : PropTypes.arrayOf(PropTypes.object),
  }).isRequired
}

export default PostCard;
