import React, {useState,useCallback} from 'react'
import { useSelector } from 'react-redux'
import PropTypes from 'prop-types';

import PostImages from '../components/PostImages'

import { Button ,Card, Avatar, Popover, List,Comment } from 'antd'
import { RetweetOutlined , HeartTwoTone , HeartOutlined , MessageOutlined , EllipsisOutlined} from '@ant-design/icons'

const PostCard = ({post}) => {

  const [liked, setLiked] = useState(false);
  const [commentFormOpened , setCommentFormOpened] = useState(false)
  //optional ì²´ì´ë‹ : ~~?.~~ ì•„ì´ë””ê°€ ìˆìœ¼ë©´ me ê°€ë“¤ì–´ê°€ê³  ì—†ìœ¼ë©´ ì–¸ë””íŒŒì¸ì´ ë“¤ì–´ê°„ë‹¤.
  const id = useSelector((state)=>state.user.me ?.id);

  const onToggleLike = useCallback(()=>{
    //false true ì„œë¡œ ë°”ê¾¸ëŠ”ë°©ë²•ìœ¼ë¡œ ì´ê²ƒë„ìˆìŒ.
    setLiked((prev)=>!prev);
  },[])
  const onToggleComment = useCallback(()=>{
    //false true ì„œë¡œ ë°”ê¾¸ëŠ”ë°©ë²•ìœ¼ë¡œ ì´ê²ƒë„ìˆìŒ.
    setCommentFormOpened((prev)=>!prev);
  },[])

  return (
    <div style = {{marginBottom : 20}}>
      <Card cover = {post.Images[0] && <PostImages images = {post.Images}/>}
      //jsxì—ì„œ ë°°ì—´ì•ˆì—ëŠ” keyë¥¼ ë„£ì–´ì£¼ì–´ì•¼ í•œë‹¤. ex)map 
      actions = {[
        <RetweetOutlined key = 'retweet'/>,
        liked ? 
          <HeartTwoTone twoToneColor = '#32211' key = 'heart' onClick = {onToggleLike}/> 
          :
          <HeartOutlined key = 'heart' onClick = {onToggleLike} />,
        <MessageOutlined key = 'comment'onClick = {onToggleComment}/>,
        <Popover key = 'more' content = {(
          <Button.Group>
            {/*ë¡œê·¸ì¸í–ˆê³ , ë‚´ ì•„ì´ë””ê°€ ì•„ì´ë””ì™€ ê°™ìœ¼ë©´ ìˆ˜ì •ì‚­ì œê°€ëŠ¥, ì•„ë‹ˆë©´ ì‹ ê³ ê°€ëŠ¥ */}
            {id && post.User.id === id ? (
              <>
              <Button>ìˆ˜ì •</Button>
              <Button type = 'danger'>ì‚­ì œ</Button>
              </>
            ) : <Button>ì‹ ê³ </Button>}
          </Button.Group>
        )}>
          <EllipsisOutlined />
        </Popover>
      ]}
      >
        <Card.Meta
        avatar = {<Avatar>{post.User.nickname[0]}</Avatar>}
        title = {post.User.nickname}
        description = {post.content}
        />
      </Card>
      {commentFormOpened && (
        <div>
          <CommentForm/>
          <List 
          header = {`${post.Comment.length}ê°œì˜ ëŒ“ê¸€`}
          itemLayout = 'horizontal'
          dataSource = {post.Comments}
          renderItem = {(item)=>(
            <li>
              <Comment 
              author = {item.User.nickname}
              avatar = {<Avatar>{item.User.nickname[0]}</Avatar>}
              content = {item.content}
              />

            </li>
          )}
          />

        </div>
      )}
    </div>
  )
}


PostCard.propTypes = {
  post: PropTypes.shape({
    id: PropTypes.number,
    User: PropTypes.object,
    content: PropTypes.string,
    createdAt: PropTypes.object,
    Comments: PropTypes.arrayOf(PropTypes.any),
    Images: PropTypes.arrayOf(PropTypes.any),
  }),
};

export default PostCard;
