  const dispatch = useDispatch();
  const [text, setText] = useState('')
  const onChangeText = useCallback((e) => {
    setText(e.target.value)
  })
  const onSubmit = useCallback(()=>{
    //onChangeText ì— ë‚¨ê¸´ê¸€ì„ í¬ìŠ¤íŒ…í•´ì•¼í•˜ë¯€ë¡œ ADD_POST ì‹¤í–‰ì‹œí‚¨ë‹¤.
    //ë””ìŠ¤íŒ¨ì¹˜ë¥¼ í•´ì¤˜ì•¼í•¨
    dispatch(addPost); //ì•¡ì…˜ì€ ê°ì²´ì„ì„ ìŠì§€ë§ˆ. 
  },[])

  //ì´ë¯¸ì§€ì—…ë¡œë“œ í´ë¦­
  const onClickImgUpload = useCallback(()=>{
    imageInput.current.click();
  }, [imageInput.current])
  //imageInput.current ì„ í†µí•´ imageInputì— ì ‘ê·¼í•  ìˆ˜ ìˆë‹¤.

  return (
    <Form style={{ margin: '10px 0 20px' }} encType="multipart/form-data" onFinish = {onSubmit}>
      <Input.TextArea value={text} onChange={onChangeText} maxLength={140} placeholder="ì–´ë–¤ ì‹ ê¸°í•œ ì¼ì´ ìˆì—ˆë‚˜ìš”?" />

      <div>
        <input type = 'file' multiple hidden ref ={imageInput} />
        <Button onClick = {onClickImgUpload}>ì´ë¯¸ì§€ ì—…ë¡œë“œ</Button>
        <Button type = 'primary' 
        style = {{float : 'right'}} htmlType = 'submit'>ì§¹ì§¹</Button>
      </div>

      <div>
        {imagePaths.map((v)=>{
          return <div key = {v} style = {{display : 'inline-block'}}>
            <img src = {v} style = {{width : '200px'}} alt = {v} />
            <div><Button>ì œê±°</Button></div>
            </div>
        })}
      </div>

    </Form>
  )
}

export default PostForm;
