import React, { useState } from 'react'
import Slick from 'react-slick'
import PropTypes from 'prop-types';
import styled from 'styled-components'

const Overlay = styled.div`
position:fixed;
z-index : 5000;
top : 0;
left : 0;
right : 0;
bottom : 0;
`

const Header = styled.header`
height : 44px;
background : white;
position : relative;
padding : 0;
text-align : center;

&h1{
  margin : 0;
  font-size: 17px;
  color : #333;
  line-height: 44px;
  cursor: pointer;
}
&button{
  position : absolute;
  right : 0;
  top : 0;
  padding :15px;
  line-height: 14px;
  cursor: pointer;
}
`
const SlickWrapper = styled.div`
  height : calc(100%-44px);
  background : #090909;
`
const ImgWrapper = styled.div`
  padding : 32px;
  text-align: center;
  &img{
    margin : 0 auto;
    max-height: 750px;
  }
`

const ImagesZoom = ({images, onClose}) => {
  const [currentSlide , setCurrentSlide] = useState(0)
  return (
  <Overlay>
    <Header>
      <h1>ìƒì„¸ì´ë¯¸ì§€</h1>
      <button onClick = {onClose}> &times </button>
    </Header>
    <SlickWrapper>
      <div>
        <Slick
          initialSlide = {0} //ì¸ë±ìŠ¤ 0ë²ˆì§¸ë¶€í„° ì‹œì‘
          afterChange = {(slide)=>setCurrentSlide(slide)} //pageë¥¼ ë„˜ê¸°ë©´ ìŠ¬ë¼ì´ë“œì— ë²ˆí˜¸ë¥¼ ì¤€ë‹¤.ê·¸ê±¸ ìŠ¤í…Œì´íŠ¸ë¡œ ì €ì¥.
          infinite 
          arrows = {false}
          slidesToShow = {1} //í•œë²ˆì— í•˜ë‚˜ì”©ë§Œ ë„˜ê¸¸ìˆ˜ìˆê²Œ
          slidesToScroll = {1}
        >
          {images.map((el)=>{
            return <ImgWrapper key = {el.src}>
              <img src = {el.src} alt = {el.src} />
            </ImgWrapper>
          })}
        </Slick>
      </div>
    </SlickWrapper>
  </Overlay>
  )
}

ImagesZoom.propTypes = {
  images : PropTypes.arrayOf(PropTypes.object).isRequired,
  onClose : PropTypes.func.isRequired,
}

export default ImagesZoom;
